		/**
		 * Rewind to first result.
		 *
		 * @return  void
		 * @access  public
		 * @since   1.0.0
		 */
		public function rewind() {
			$this->position = 0;
		}

		/**
		 * Return current element.
		 *
		 * @return  object
		 * @access  public
		 * @since   1.0.0
		 */
		public function current() {
			return $this->results[ $this->position ];
		}

		/**
