 *
 * @return 	boolean
 * @since 	1.0.0
 */
function charitable_is_donation_receipt_page() {
	global $wp_query;

	$receipt_page = charitable_get_option( 'donation_receipt_page', 'auto' );

	if ( 'auto' != $receipt_page ) {
