			die();
		} else {
			if ( 'default_templates' === $template_type ) {
				$this->getBackendDefaultTemplate();
				die();
			} else {
				echo apply_filters( 'vc_templates_render_backend_template', $template_id, $template_type );
				die();
			}
		}
